import React from 'react';

class Signin extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            signInEmail:' ',
            signInPassword: ' '
        }
    }
   onEmailChange = (event) => {
        this.setState({signInEmail: event.target.value})
   }

   onPasswordChange = (event) => {
    this.setState({signInPassword: event.target.value})
    }
    
    onSubmitSignIn = () => {
        fetch('http://localhost:3000/signin', {
          method: 'pst',
          headers: {'Content-Type': 'application/json'},
          body: JSON.stringify({
            email: this.state.signInEmail,
            password: this.state.signInPassword
          })
        })
          .then(response => response.json())
          .then(user => {
            if (user.id) {
              this.props.loadUser(user)
              this.props.onRouteChange('home');
            }
          })
      }
   
    render(){
        const {onRouteChange} = this.props;
        return (
            <article className="br4 ba dark-gray b--black-10 mv3 w-100 w-50-m w-25-l mw6 shadow-5 center">
                <main className="pa4 black-80">
                        <form className="measure ">
                        <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                    <legend className="f1 fw6 ph0 mh0">Sign In</legend>
                    <div className="mt3">
                        <label className="db fw6 lh-copy f4" for="email-address">Email</label>
                        <input onChange={this.onEmailChange} className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="email" name="email-address"  id="email-address" />
                    </div>
                    <div className="mv3">
                        <label className="db fw6 lh-copy f4" for="password">Password</label>
                        <input onChange={this.onPasswordChange} className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" type="password" name="password"  id="password" />
                    </div>
                    
                    </fieldset>
                    <div className="">
                    <input  onClick={this.onSubmitSignIn} className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f4 dib" type="submit" value="Sign in" />
                    </div>
                    <div className="lh-copy mt3">
                    <p onClick={() => onRouteChange('r  egister')} href="#0" className="f4 link dim black db">Registre-se</p>
                    
                    </div>
                    </form>
                </main>
            </article>
            );
    }  
}

export default Signin;
